     * Load window brows file     */    function getFile(_file,view_picture,file_name,picture)    {        document.getElementById(_file).click();        upload(_file,view_picture,file_name,picture);    }// function    function upload(_file,view_picture,file_name,picture){        var input = document.getElementById(_file),            formdata = false;        if (window.FormData) {            formdata = new FormData();        }        input.addEventListener("change", function (evt) {            document.getElementById(file_name).innerHTML = "Uploading . . ."            var i = 0, len = this.files.length, img, reader, file;            for ( i; i < len; i++ ) {                file = this.files[i];                if ( window.FileReader ) {                    reader = new FileReader();                    reader.onloadend = function (e) {                        showUploadedItem(e.target.result, file.fileName);                    };                    reader.readAsDataURL(file);                }                if (formdata) {                    formdata.append(_file, file);                }            }            if (formdata) {                jQuery.ajax({                    url: "<?php echo site_url();?>backend/c_file/multiple_upload_file/tours/" + _file,                    type: "POST",                    data: formdata,                    processData: false,                    contentType: false,                    success: function (res) {                        document.getElementById(view_picture).innerHTML = '<img width="320px" height="228px" src="<?= base_url().'upload/rooms/' ?>'+res+'" />';                        document.getElementById(file_name).innerHTML = res;                        document.getElementById(picture).value = res;                        formdata = new FormData();//clear the old file that remember                    }                });            }        }, false);    }//end main fnc upload img    //      delete file    //    function delete_file(file,view_picture,plz_upload,image_name){        var result = confirm("Are you sure you want to delete?");        if (result == true) {            $.ajax({                type: "POST",                url: "<?php echo site_url();?>backend/c_file/delete_file/tours/" + document.getElementById(image_name).value,                data: '', // paramenter & value                cache: false,                success: function(){                    document.getElementById(view_picture).innerHTML = '';                    document.getElementById(plz_upload).innerHTML = "";                    document.getElementById(file).value = "";                    document.getElementById(image_name).value = "";                }            });        }    }    $(document).ready(function(){        $('#main_cat_id').change(function(){            msg = 'main_cat_id='+$(this).val();            $.ajax({                type : 'POST',                url : '<?php echo site_url();?>backend/tours/ajax_subcategory',                data : msg,                success: function(categories)                {                    var jsonList = {"Table" : categories}                    var listItems= "";                    listItems+= '<div id="uniform-main_cat_id" class="selector">';                    listItems+= '<select name="cat_id"  id="cat_id" class="validate[required]">';                    for (var i = 0; i < jsonList.Table.length; i++){                        listItems+= "<option value='" + jsonList.Table[i].id + "'>" + jsonList.Table[i].name + "</option>";                    }                    listItems+= "</select>";                    $("#sub_category").html(listItems);                } //end success            });        });    });// end ready			</script><script>    CKEDITOR.re